import { Injectable } from '@angular/core';
import {Evenement} from '../models/evenement.model';
import {Media} from '../models/media.model';
import {DatePipe} from '@angular/common';

@Injectable({
  providedIn: 'root'
})
export class EvenementService {

  private _evenement: Evenement;

  private _evenements: Array<Evenement>;
  private media: Media;
  private datePipe: DatePipe;

  constructor() {
    this._evenements = new Array<Evenement>();
    for (let i = 0; i < 5; i++) {
      this.evenement = new Evenement();
      this.evenement.titre = 'evenement ' + i;
      this.evenement.sousTitre = 'sous titre';
      this.evenement.isPublier = true;
      this.media = new Media();
      if (i === 2) {
        this.media.url = 'data:image/jpeg;base64,';
      } else {
        this.media.url = 'data:image/png;base64,';
      }
      this.evenement.medias = new Array<Media>();
      this.evenement.medias.push(this.media);
      this.evenements.push(this.evenement);
    }
  }

  get evenement(): Evenement {
    if (this._evenement == null) {
      this._evenement = new Evenement();
    }
    return this._evenement;
  }

  set evenement(_evenement: Evenement) {
    this._evenement = _evenement;
  }

  get evenements(): Array<Evenement> {
    if (this._evenements == null) {
      this._evenements = new Array<Evenement>();
    }
    return this._evenements;
  }

  set evenements(_evenements: Array<Evenement>) {
    this._evenements = _evenements;
  }

  public getUpComingEvents(): Array<Evenement> {
    return this.evenements;
  }


  public getTop3Events(): Array<Evenement> {
    return this.evenements;
  }
}
